import React,{useState,useEffect} from 'react'
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import {useParams} from 'react-router-dom'
import axios from '../../node_modules/axios'
import {useNavigate} from 'react-router-dom'
import Finish from './Finish'
function Test() {
let {id}=useParams();
let {qnumber}=useParams();
let navigate=useNavigate()
let name1;
let option1=[];
let type1=[]
const [repo,setRepo]=useState([])
let a1    
let intnumber=parseInt(qnumber)
let inc=1+intnumber
let strinc=inc.toString()

   let text1=[]
const getRepo=()=>{
    axios.get('http://interviewapi.stgbuild.com/getQuizData' )
    .then(response => {
        const myRepo=response.data.tests;
       setRepo(myRepo);

    });
};

let len
 useEffect(() => {
 getRepo()    
    }, [])


for(let i=0;i<repo.length;i++){
  if(repo[i]._id == id){
    name1=repo[i].name
      text1=repo[i].questions[qnumber]
      option1=repo[i].questions[qnumber].options
     type1=repo[i].questions[qnumber].type
   len=repo[i].questions.length

  }
}


for(let i=0;i<repo.length;i++){
  if(repo[i]._id == id){
    a1=repo[i].questions[qnumber].correctOptionIndex

  }
}
console.log(a1)
function finish(){
  navigate('/finish')
}
    return (
        <div>
            <div>
             <h1>My Interview Portal</h1>
             </div>


      <div class='card'>
            <div class="card-body">

        <h5 class="card-header">{name1}</h5>

      </div>
      <div class="card-body ">
      <form style={{textAlign:'left'}}> 
                            <label>{text1.questionText}</label>

                            {
                            type1 === 'Multiple-Response' ?
                                option1.map((note,index)=>(

                                  <div class="checkbox" >

                                  <input key={note.id} type="checkbox" name="option" value={index}/> {option1[index]}

                          </div>  ))

                              :
                            option1.map((note,index)=>(

                                <div class="radio" >

                                <input key={note.id} type="radio" name="option" value={index}/> {option1[index]}

                        </div>
                            ))}

                        </form>
      </div>

      <div class="footer">
        {(len) == (strinc)?

        <button type="button" class="btn btn-danger"  onClick={()=>finish()}>Finish</button>
        :
        <>
        <a href={"/test/"+id+"/"+strinc} class="btn btn-success mx-2">Next</a>
        <button type="button" class="btn btn-danger" onClick={()=>finish()}>Finish</button>
        </>
        }


</div>
</div>
        </div>
    )
}

export default Test
