  if((repo[index].questions[qnumber].correctOptionIndex) == seloption){
    // myLeads[id].answer.push('correct'
      //repo.push('correct')
     myLeads.answer.push('correct')
  }else{
   // repo.push('wrong')
// myLeads[id].answer.push('wrong')
myLeads.answer.push('wrong')
  }
