      <>

      <div class="row">
      <h1>My Interview Portal</h1>
          <hr/>
          <div class="col-md-12">
              <div class="panel panel-default">
                  <div class="panel-heading">{name1} </div>
                  <div class="panel-body">
                  <form style={{textAlign:'left'}}> 
                            <label>{text1.questionText}</label>

                            {
                            type1 === 'Multiple-Response' ?
                                option1.map((note,index)=>(

                                  <div class="checkbox" >

                                  <input onChange={clickableRadio} ref={textValue1} key={note.id} type="checkbox" name="option" value={index} /> {option1[index]}

                          </div>  ))

                              :

                            option1.map((note,index)=>(

                                <div class="radio" >

                                <input onChange={clickableRadio} ref={textValue1} key={note.id} type="radio" name="option" value={index} /> {option1[index]}

                        </div>
                            ))}

                        </form>
                  </div>
                  <div class="panel-footer">
                      {(len) == (strinc)?
                      <>
                        <a  class=" btn btn-danger"  style={{textAlign:'right'}} onClick={()=>finish()} >Finish</a>  
                        </>
                        :
                        <>
                        <a  href={"/test/"+id+"/"+strinc} class="btn btn-success" style={{textAlign:'left'}} onClick={next} >Next</a>
                        <a class=" btn btn-danger" style={{textAlign:'right'}} onClick={()=>finish()} >Finish</a>
                        {/* <a class=" btn btn-danger" style={{textAlign:'right'}} onClick={()=>increment()} >increment</a> */}
                        </>
                    }


                  </div>
              </div>
          </div>
      </div>

  </>
    )
}

export default Test
